package slidingWindow;

import java.util.HashMap;

/**
 * 给定两个字符串s1和s2，写一个函数来判断 s2 是否包含 s1的排列。
 *
 * 换句话说，第一个字符串的排列之一是第二个字符串的子串。
 *
 * 示例1:
 *
 * 输入: s1 = "ab" s2 = "eidbaooo"
 * 输出: True
 * 解释: s2 包含 s1 的排列之一 ("ba").
 * 
 *
 * 示例2:
 *
 * 输入: s1= "ab" s2 = "eidboaoo"
 * 输出: False
 *
 */
public class Test_567 {
    public static boolean checkInclusion(String s1, String s2) {
        if (s2.length() < 1 || s2 == null || s2.length() < s1.length()) {
            return false;
        }

        HashMap<Character, Integer> window = new HashMap<>();
        HashMap<Character, Integer> needs = new HashMap<>();

        for (int i = 0; i < s1.length(); i++) {
            Character character = s1.charAt(i);
            needs.put(character, needs.getOrDefault(character, 0) + 1);
        }

        int left = 0, right = 0, match = 0;
        while (right < s2.length()) {
            Character character_right = s2.charAt(right);
            if (needs.containsKey(character_right)) {
                window.put(character_right, window.getOrDefault(character_right, 0) + 1);
                /**注意！！！不能直接用==*/
                if (window.get(character_right).equals(needs.get(character_right))) {
                    match++;
                }
            }
            right++;

            while (match == needs.size()) {
                if (right - left == s1.length()) {
                    return true;
                }
                Character character_left = s2.charAt(left);
                if (needs.containsKey(character_left)) {
                    window.put(character_left, window.getOrDefault(character_left, 0) - 1);
                    if (window.get(character_left) < needs.get(character_left)) {
                        match--;
                    }
                }
                left++;
            }
        }
        return false;
    }

    public static void main(String[] args) {

        String s1 = "";
        String s2 = "";
        System.out.println(checkInclusion(s1, s2));
    }
}
